settings.custom_liquid:
{% comment %}
Add this as a Custom Liquid block in your page sections
{% endcomment %}

{% unless customer.tags contains 'healthcare_provider' %}
<style>
  body.modal-open {
    overflow: hidden;
  }
</style>

<div id="providerModal" style="display: none; position: fixed; top: 0; left: 0; width: 100%; height: 100%; background-color: rgba(0, 0, 0, 0.5); z-index: 9999;">
  <div style="position: fixed; top: 50%; left: 50%; transform: translate(-50%, -50%); background-color: white; padding: 2rem; border-radius: 0.5rem; max-width: 500px; width: 90%;">
    <div style="margin-bottom: 1.5rem;">
      <h2 style="font-size: 1.25rem; font-weight: bold; color: #333;">Healthcare Provider Confirmation</h2>
    </div>
    <div style="display: flex; align-items: flex-start; gap: 0.5rem; margin-bottom: 1.5rem;">
      <input 
        type="checkbox" 
        id="providerCheckbox" 
        style="margin-top: 0.25rem;"
      >
      <label for="providerCheckbox">
        I confirm that I am a licensed healthcare provider and am authorized to access this content.
      </label>
    </div>
    <button 
      id="confirmButton" 
      disabled
      style="background-color: #000; color: white; padding: 0.75rem 1.5rem; border: none; border-radius: 0.25rem; cursor: pointer; width: 100%; opacity: 0.5;"
    >
      Confirm
    </button>
  </div>
</div>

<script>
document.addEventListener('DOMContentLoaded', function() {
  const pageUrl = window.location.pathname;
  const storageKey = 'healthcareProviderConfirmed_' + pageUrl;

  if (!localStorage.getItem(storageKey)) {
    document.getElementById('providerModal').style.display = 'block';
    document.body.classList.add('modal-open');
  }

  document.getElementById('providerCheckbox').addEventListener('change', function(e) {
    const button = document.getElementById('confirmButton');
    button.disabled = !e.target.checked;
    button.style.opacity = e.target.checked ? '1' : '0.5';
  });

  document.getElementById('confirmButton').addEventListener('click', function() {
    if (document.getElementById('providerCheckbox').checked) {
      localStorage.setItem(storageKey, 'true');
      document.getElementById('providerModal').style.display = 'none';
      document.body.classList.remove('modal-open');

      {% if customer %}
        fetch('/admin/api/2024-01/customers/{{ customer.id }}/tags.json', {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            tags: [...'{{ customer.tags }}'.split(','), 'healthcare_provider']
          })
        });
      {% endif %}
    }
  });
});
</script>
{% endunless %}
